(4, 'Trial End', 'Your trial period end', '<!-- BEGIN: Content -->\r\n<table class="container content boxshadow5" align="center"> <tbody><tr><td>\r\n      <table class="row note"><tbody><tr><td class="wrapper last">\r\n            <p> Dear {Customer name},</p>\r\n            <p> Your trial period has been ended. Purchase a plan today and resume business from us. </p>\r\n            <div class="devider"></div>\r\n            <p class="bold">Login details:</p>\r\n            <p>Email address : {email}</p>\r\n            <p> Thanks, </p>\r\n            <p> {Shopify clone} </p></td> </tr> </tbody>\r\n      </table>  </td>  </tr>  </tbody>\r\n</table> <!-- END: Content -->', 1, '0000-00-00', '2015-02-23'),
(5, 'Order Cancelled', 'Order Cancelled', '<table class="container content" align="center"> <tbody><tr> <td>\r\n  <table class="row note">\r\n    <tbody> <tr> <td class="wrapper last">\r\n          <p>Dear {Customer name},</p>\r\n          <p> Greetings from {Store name} <strong></strong> </p>\r\n          <p> As per your request the following order/items in order &nbsp;ID {orderid} has been cancelled successfully. </p><br>\r\n          <p><strong> {note}</strong></p>\r\n          <p><strong>{Address}</strong></p>\r\n          <p> Thanks, </p>\r\n          <p> {Store name} </p>  </td> </tr>\r\n    </tbody>\r\n  </table> </td> </tr>\r\n</tbody></table>', 1, '0000-00-00', '2015-02-24'),
