(8, 'Shipment to sellar', 'Order ID is ready to shipped', '<table class="container content" align="center"> <tr> <td>\r\n	<table class="row note">\r\n		<tbody> <tr> <td class="wrapper last">\r\n	        <p>Dear {Store name},</p>\r\n	        <p> Order ID {orderid} shipment confirmation have been sent to the customer mail.</p> </p><br>\r\n	        <p><strong> {note}</strong></p>\r\n	        <p><strong>{Address}</strong></p> </td> </tr>\r\n		</tbody>\r\n	</table> </td> </tr>\r\n</table>', 1, '0000-00-00', '2015-02-23'),
(9, 'Shipment to Customer', 'Order ID is ready to shipped', '<table class="container content" align="center"> <tbody><tr> <td>\r\n      <table class="row note">\r\n            <tbody> <tr> <td class="wrapper last">\r\n              <p>Dear {Customer name},</p>\r\n              <p> Greetings from {Store name} <strong></strong> </p>\r\n              <p> We are pleased to inform you that following items in your Order&nbsp;ID {orderid} have been packed by the seller and are ready to be shipped, you can receive your product soon. </p><br>\r\n              <p><strong> {note}</strong></p>\r\n              <p><strong>{Address}</strong></p>\r\n              <p> Thanks, </p>\r\n              <p> {Store name} </p>  </td> </tr>\r\n            </tbody>\r\n      </table> </td> </tr>\r\n</tbody></table>', 1, '0000-00-00', '2015-03-11'),
(10, 'Order details mail', 'Order details [with Order ID] in store name', '<table class="container content" align="center"> <tbody><tr> <td>\r\n      <table class="row note">\r\n            <tbody> <tr> <td class="wrapper last">\r\n              <p> Dear {Customer name},</p>\r\n             Thank you for shopping with us <br>\r\n             Your order details below for the order ID <i>{orderid}</i> <br>\r\n              </p><p> <strong> {note}</strong></p>\r\n              <p><strong>{Address}</strong> </p>\r\n              <p>Thanks,</p>\r\n              <p> {Store name} </p><p> </p>  </td> </tr>\r\n            </tbody>\r\n      </table> </td> </tr>\r\n</tbody></table>', 1, '0000-00-00', '2015-02-23'),
(11, 'Order sellar Mail', 'New order [ Order ID ] from customer name', '<table class="container content" align="center"> <tbody><tr> <td>\r\n      <table class="row note">\r\n            <tbody> <tr> <td class="wrapper last">\r\n              <p> Dear {Store name},</p>\r\n             Your beloved customer have purchased item in your store, please check it and details are mention below for the order ID  <i>{orderid}</i> <br>\r\n              </p><p> <strong> {note}</strong></p>\r\n              <p><strong>{Address}</strong> </p>\r\n              <p>Thanks,</p>\r\n              <p> {Customer name} </p><p> </p>  </td> </tr>\r\n            </tbody>\r\n      </table> </td> </tr>\r\n</tbody></table>', 1, '0000-00-00', '2015-02-23'),
(12, 'Refund mail', 'Refund notification', '<!-- BEGIN: Content -->\r\n<table class="container content boxshadow5" align="center"> <tbody><tr> <td>\r\n      <table class="row note"> <tbody><tr> <td class="wrapper last">\r\n            <p> Dear {Customer name},</p>\r\n            <p> <i>{Store name}</i> have processed  refund for the following  your order in <i>{Orderid}</i>. The amount  of <i>{Amount}</i> towards the order/item will be refunded to your bank card/account. </p>\r\n            <p> Thanks, </p>\r\n            <p> {Shop name} </p>\r\n            <p><strong>Note:</strong> Order summary details display underneath to it.</p> \r\n            </td> </tr> </tbody>\r\n      </table> </td> </tr> </tbody>\r\n</table>\r\n<!-- END: Content -->', 1, '0000-00-00', '2015-02-23'),
(13, 'Store signup', 'Sub: Welcome to shopify clone', '<table class="container content boxshadow5" align="center">\n            <tbody><tr>\n                  <td>\n                        <table class="row note">\n                        <tbody><tr>\n                              <td class="wrapper last">\n                                    <p> Dear {sellar name},</p>\n                                    <p><i>Greeting from {SERVER_NAME} </i></p>\n                                    <p>\n                                          We hearty congratulate you for creating an online store with us. Now you are in a trial period which will expire in 14 days, upgrade for accessing unlimited.\n                                    </p>\n                                     <div class="devider"></div>\n                                    <p class="bold">click here to login</p>\n                                    <p style="color:#FF0000;">{CLICK_HERE_TO_LOGIN}</p>                \n                                    \n                              </td>\n                        </tr>\n                        </tbody></table>\n                  </td>\n            </tr>\n            </tbody></table>', 1, '0000-00-00', '2015-02-23'),
