(17, 'Upgraded Mail', 'Thank you for upgrading', '<table class="container content" align="center"> <tbody><tr> <td>\r\n    <table class="row note">\r\n        <tbody> <tr> <td class="wrapper last">\r\n            <p>Dear {store name},</p>\r\n            <p>We are pleasure to invite you for the newly upgrade plan {Plan name} until {validity}. We wish you all the best wishes for business growth.</p>\r\n            <p> Thanks, </p>\r\n            <p> Shoppieeshop. </p> </td> </tr>\r\n        </tbody>\r\n    </table> </td> </tr>\r\n</tbody></table>', 1, '2015-06-06', '2015-06-06'),
(18, 'Customer activation storeadmin', 'Account activation', '<table class="container content" align="center"> <tbody><tr> <td>\r\n      <table class="row note">\r\n            <tbody> <tr> <td class="wrapper last">\r\n                  <p>Dear {firstname},</p>\r\n                  <p> Welcome greetings from {store name},</p>\r\n                  <p>We have created an account for you, just one click <a href="{activation}"> here </a>  to confirm your email address.</p> \r\n                  </td> </tr>\r\n            </tbody>\r\n      </table> </td> </tr>\r\n</tbody></table>', 1, '2015-06-06', '2015-07-16'),
